// Dependencies
import React, { Component } from "react";
import { Link } from "react-router-dom";
import Utils from "../utils/utils";

// Redux
import PropTypes from "prop-types";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";

// Material UI
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

// Custom Actions


// START IMPORT ACTIONS
import InterestsActions from "../redux/actions/InterestsActions";
import ScoresActions from "../redux/actions/ScoresActions";

// END IMPORT ACTIONS

/** APIs

* actionsInterests.create
*	@description CRUD ACTION create
*
* actionsScores.findByInterest
*	@description CRUD ACTION findByInterest
*	@param Objectid key - Id della risorsa Interest da cercare
*
* actionsInterests.get
*	@description CRUD ACTION get
*	@param ObjectId id - Id 
*
* actionsInterests.update
*	@description CRUD ACTION update
*	@param ObjectId id - Id
*

**/

class InterestsEdit extends Component {
  // Init interests
  constructor(props) {
    super(props);
    this.state = {
      interests: {}
    };
  }

  // Load data on start
  componentDidMount() {
    if (this.props.match.params.id !== "new") {
      this.props.actionsInterests.loadInterests(this.props.match.params.id);
      this.props.actionsScores.findByInterest(this.props.match.params.id);
    }
    
  }

  // Insert props interests in state
  componentWillReceiveProps(props) {
    this.setState(...this.state, {
      interests: props.interests
    });
  }

  // Save data
  save(event) {
    event.preventDefault();
    if (this.state.interests._id) {
      this.props.actionsInterests.saveInterests(this.state.interests).then(data => {
        this.props.history.push("/interestses/");
      });
    } else {
      this.props.actionsInterests.createInterests(this.state.interests).then(data => {
        this.props.history.push("/interestses/");
      });
    }
  }

  // Show content
  render() {
    return (
      <div>
        <h1>Interests Edit</h1>
        <form className="myForm" onSubmit={this.save.bind(this)}>

          
          <TextField
            id="Name"
            label="Name"
            value={this.state.interests.Name || ""}
            onChange={Utils.handleChange.bind(this, "interests")}
            margin="normal"
            fullWidth
          />
          
          {/* RELATIONS */}

          {/* EXTERNAL RELATIONS */}
          
          {/* External relation with Scores */}
          
          <h3>Scores</h3>
          {(!this.props.listScores || this.props.listScores.length === 0) && 
            <div>No Scores associated</div>
          }
          {this.props.listScores &&
            this.props.listScores.map((item, i) => {
              return (
                <Link to={"/scoress/" + item._id} key={item._id}>
                  {item._id}
                </Link>
              );
            })}

          
          {/* Footer */}
          <div className="footer-card">
            <Link to="/interestses/">Back to list</Link>

            <Button type="submit" variant="contained" color="primary">
              Save
            </Button>
          </div>
        </form>
      </div>
    );
  }
}

// Store actions
const mapDispatchToProps = function(dispatch) {
  return { 
    actionsInterests: bindActionCreators(InterestsActions, dispatch),
    actionsScores: bindActionCreators(ScoresActions, dispatch),
  };
};

// Validate types
InterestsEdit.propTypes = { 
  actionsInterests: PropTypes.object.isRequired,
  actionsScores: PropTypes.object.isRequired,
};

// Get props from state
function mapStateToProps(state, ownProps) {
  return {
    interests: state.InterestsEditReducer.interests,
    listScores: state.InterestsEditReducer.listScores
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(InterestsEdit);
